func:
// Input: timestamp trigger from the inject node.
// Output: CIP get_attribute_single request targeting assembly instance 100 attribute 3.
msg.payload = {
    service: 'get_attribute_single',
    objectType: 'assembly',
    instance: 100,
    attribute: 3
};
return msg;

// Input: Buffer payload from CIP node representing IEEE-754 float watts.
// Output: Array of InfluxDB line protocol objects expected by node-red-contrib-influxdb.
const buf = Buffer.from(msg.payload);
// Interpret the first 4 bytes as a 32-bit float. Adjust parsing as needed for your device.
const watts = buf.length >= 4 ? buf.readFloatLE(0) : 0;
msg.payload = [{
    measurement: 'pm500_assembly',
    fields: {
        watts
    },
    tags: {
        source: 'pm500',
        attribute: '3'
    }
}];
return msg;
