func:
// Input: timer trigger message.
// Output: CIP request describing assembly instance 100 attribute 3.
msg.payload = {
    service: 'get_attribute_single',
    objectType: 'assembly',
    instance: 100,
    attribute: 3
};
return msg;

// Input: Buffer payload returned from the CIP out node.
// Output: JSON string ready for MQTT publish plus topic metadata.
const buf = Buffer.from(msg.payload);
const watts = buf.length >= 4 ? buf.readFloatLE(0) : 0;
msg.payload = JSON.stringify({
    source: 'pm500',
    attribute: 3,
    watts,
    timestamp: Date.now()
});
msg.topic = 'pm500/assembly/100/attribute/3';
return msg;
